import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { loadScript } from 'src/utils/load-script';
import { waitDefineWebComponent } from 'src/utils/wait-define-web-component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  @ViewChild('container', { read: ElementRef })
  container!: ElementRef;
  loading = true;

  buttonMfeUrl = 'http://localhost:5002/assets/mfe-button.js';
  buttonTag = 'mfe-button';

  async ngOnInit() {
    try {
      await loadScript(this.buttonMfeUrl);
      await waitDefineWebComponent(this.buttonTag);
    } finally {
      this.loading = false;
    }
  }
}
<div *ngIf="!loading; else loadingContainer">
  <mfe-button text="Adicionado pelo angular"></mfe-button>
</div>

<ng-template #loadingContainer>
  <p>Carregando MFE do React</p>
</ng-template>

<router-outlet></router-outlet>
