import { Component } from '@angular/core';
import { loadScript } from 'src/utils/load-script';
import { waitDefineWebComponent } from 'src/utils/wait-define-web-component';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  loading = true;

  tags = {
    react: 'mfe-button',
    angular: 'angular-card',
  };

  scripts = {
    react: `http://localhost:5002/assets/${this.tags.react}.js`,
    angular: 'http://localhost:8080/web-component.js',
  };

  async ngOnInit() {
    try {
      await Promise.all([
        loadScript(this.scripts.react),
        loadScript(this.scripts.angular),
        waitDefineWebComponent(this.tags.react),
        waitDefineWebComponent(this.tags.angular),
      ]);
    } finally {
      this.loading = false;
    }
  }
}
<div *ngIf="!loading; else loadingContainer">
  <mfe-button
    text="MFE React adicionado pelo Angular"
    icon="arrow_right"
  ></mfe-button>

  <angular-card title="Titulo aqui"></angular-card>
</div>

<ng-template #loadingContainer>
  <p>Carregando MFE do React</p>
</ng-template>
