import { Component } from '@angular/core';
import { loadScript } from 'src/utils/load-script';
import { waitDefineWebComponent } from 'src/utils/wait-define-web-component';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  loading = true;

  buttonMfeUrl = 'http://localhost:5002/assets/mfe-button.js';
  buttonTag = 'mfe-button';
  buttonFncKey = 'goToReactRoute';

  async ngOnInit() {
    try {
      await loadScript(this.buttonMfeUrl);
      await waitDefineWebComponent(this.buttonTag);

      (window as any)[this.buttonFncKey] = () => {
        window.location.href = '/react-routes';
      };
    } finally {
      this.loading = false;
    }
  }
}
<div *ngIf="!loading; else loadingContainer">
  <mfe-button
    text="Adicionado pelo angular"
    icon="arrow_right"
    [attr.onPressKey]="buttonFncKey"
  ></mfe-button>
</div>

<ng-template #loadingContainer>
  <p>Carregando MFE do React</p>
</ng-template>
