import { DOCUMENT } from '@angular/common';
import {
  AfterViewInit,
  Component,
  ElementRef,
  inject,
  ViewChild,
} from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements AfterViewInit {
  @ViewChild('container', { read: ElementRef })
  container!: ElementRef;
  loading = false;

  constructor(@inject(DOCUMENT) private document: Document) {}

  async ngAfterViewInit() {
    try {
      this.loading = true;

      await this.loadScript();
      await this.loadMfeButton();
    } finally {
      this.loading = false;
    }
  }

  loadScript() {
    const src = 'http://localhost:5002/assets/mfe-button.js';
    const type = 'module';

    return new Promise((resolve, reject) => {
      if (typeof document === 'undefined') {
        reject('loadScript: document não está definido');
      }

      const hasScript = document.querySelector(`script[src^="${src}"]`);

      if (hasScript) {
        resolve(true);
      }

      const script = document.createElement('script');

      script.src = src;
      script.type = type;
      script.async = true;

      script.onload = () => {
        console.log('@@@ carregou o mfe');
        resolve(true);
      };

      script.onerror = () => {
        reject(`loadScript: ocorreu um erro ao carregar o script: ${src}`);
      };

      document.body.appendChild(script);
    });
  }

  loadMfeButton() {
    const tag = 'mfe-button';

    return new Promise((resolve) => {
      customElements.whenDefined(tag).then(() => {
        const webComponent = document.createComment(tag);

        console.log(webComponent);

        this.container.nativeElement.appendChild(webComponent);

        resolve(webComponent);
      });
    });
  }
}
