import { Component } from '@angular/core';
import { loadScript } from 'src/utils/load-script';
import { waitDefineWebComponent } from 'src/utils/wait-define-web-component';

@Component({
  selector: 'app-react-router',
  templateUrl: './react-router.component.html',
  styleUrls: ['./react-router.component.scss'],
})
export class ReactRouterComponent {
  loading = true;

  tag = 'mfe-router';
  script = `http://localhost:5002/assets/${this.tag}.js`;

  async ngOnInit() {
    try {
      await loadScript(this.script);
      await waitDefineWebComponent(this.tag);
    } finally {
      this.loading = false;
    }
  }
}
<div *ngIf="!loading; else loadingContainer">
  <mfe-router></mfe-router>
</div>

<ng-template #loadingContainer>
  <p>Carregando MFE do React</p>
</ng-template>
